<?require_once("models/config.php");if(isUserLoggedIn()) {	echo '<meta http-equiv="refresh" content="0; URL=index.php?page=account">';	die(); }if(!empty($_POST)) {	$errors = array();		if($_SESSION["Reset_Attempts"] > 4)	{		$uagent = mysql_real_escape_string(getuseragent()); //get user agent		$ip = mysql_real_escape_string(getIP()); //get user ip		$account = mysql_real_escape_string("Guest/Not Logged In");		$date = mysql_real_escape_string(gettime());		$sql = @mysql_query("INSERT INTO access_violations (username, ip, user_agent, time) VALUES ('$account', '$ip', '$uagent', '$date');");		$errors[] = "Access Denied!";	}	$captcha = md5($_POST["captcha"]);				if ($captcha != $_SESSION['captcha'])	{		$errors[] = lang("CAPTCHA_FAIL");	}	$username = trim($_POST["username"]);	if($username == "")	{		$errors[] = lang("ACCOUNT_SPECIFY_USERNAME");	}	$email = trim($_POST["email"]);	if($email == "")	{		$errors[] = "no email address given";	}	if(count($errors) == 0)	{		if(!usernameExists($username))		{			$errors[] = "Invalid Account Info";		}else{				$info = mysql_query("SELECT * FROM userCake_Users WHERE `Email`='$email' AND `Username`='$username'");		if(!$info){			$errors[] = "Invalid Account OR other error.";		}else{						echo "send an email to admin@openex.pw from your email address: ".$email." requesting a password change.</br> 			we will contact you with your new credentials. ";				}				}	}			if(count($errors) > 0)			{				if(!isset($_SESSION["Reset_Attempts"]))				{					$_SESSION["Reset_Attempts"] = 1;				}				else				{					$_SESSION["Reset_Attempts"]++;				}								echo '<ul class="nobullets">';				foreach($errors as $key => $value) {								echo '<li>'.$value.'</li>';								}				echo '</ul>';			} }else{?><h2>Forgotten Password</h2><h4 style="color: red;">Enter Your email address, account name and complete the captcha to reset your password</h4><table>	<form method="POST" action="index.php?page=reset" autocomplete="off" onsubmit="document.getElementById('#resetbutton').disabled = 1;">	<tr>		<td>			<input name="email" type="text" placeholder="Enter Your Email Address" class="field">		</td>	</tr>	<tr>		<td>			<input name="username" type="text" placeholder="Enter Your Username" class="field">		</td>	</tr>	<tr>		<td>			<center><img src="pages/docs/captcha.php" class="captcha"></center>		</td>	</tr>	<tr>		<td>			<input name="captcha" type="text" placeholder="Enter Security Code" class="field">		</td>	</tr>	<tr>		<td>			<input type="submit" id="resetbutton" class="blues"/>		</td>	</tr>	</form></table><?php}?>